import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  Box,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8'];

const AdminDashboard = () => {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedRequest, setSelectedRequest] = useState(null);
  const [response, setResponse] = useState('');
  const [dialogOpen, setDialogOpen] = useState(false);
  const [detailsDialogOpen, setDetailsDialogOpen] = useState(false);
  const [nameFilter, setNameFilter] = useState('');
  const [typeFilter, setTypeFilter] = useState('all');
  const [chartData, setChartData] = useState([]);
  const [requestTypes, setRequestTypes] = useState([]);

  useEffect(() => {
    fetchRequests();
    const interval = setInterval(fetchRequests, 5000); // Poll every 5 seconds
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    // Process data for charts when requests change
    const processChartData = () => {
      // Count requests by user
      const userRequests = {};
      const types = new Set();

      requests.forEach((request) => {
        userRequests[request.userName] = (userRequests[request.userName] || 0) + 1;
        types.add(request.type);
      });

      // Convert to chart format
      const chartData = Object.entries(userRequests).map(([name, count]) => ({
        name,
        requests: count,
      }));

      setChartData(chartData);
      setRequestTypes(Array.from(types));
    };

    processChartData();
  }, [requests]);

  const fetchRequests = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/requests/all');
      setRequests(res.data.data);
    } catch (error) {
      console.error('Error fetching requests:', error);
    }
    setLoading(false);
  };

  const handleRequestAction = async (request, status) => {
    setSelectedRequest(request);
    if (status === 'pending') {
      setDialogOpen(true);
    } else {
      setResponse('');
      await handleSubmitResponse(request, status);
      // Immediately update the request status in the UI
      setRequests(prevRequests =>
        prevRequests.map(r =>
          r._id === request._id
            ? { ...r, status, adminResponse: response }
            : r
        )
      );
    }
  };

  const handleSubmitResponse = async (request, status) => {
    try {
      await axios.put(`http://localhost:5000/api/requests/${request._id}`, {
        status,
        adminResponse: response,
      });
      setDialogOpen(false);
      setResponse('');
      fetchRequests();
    } catch (error) {
      console.error('Error updating request:', error);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'approved':
        return 'success';
      case 'rejected':
        return 'error';
      default:
        return 'warning';
    }
  };

  const handleCloseDialog = () => {
    setDialogOpen(false);
    setResponse('');
    setSelectedRequest(null);
  };

  if (loading) {
    return (
      <Container>
        <Typography>Loading...</Typography>
      </Container>
    );
  }

  // Filter requests based on name and type
  const filteredRequests = requests.filter((request) => {
    const nameMatch = request.user?.name?.toLowerCase().includes(nameFilter.toLowerCase());
    const typeMatch = typeFilter === 'all' || request.subject === typeFilter;
    return nameMatch && typeMatch;
  });

  return (
    <Container maxWidth="lg">
      <Box sx={{ mt: 4, mb: 4 }}>
        <Typography variant="h4" gutterBottom>
          Admin Dashboard - Manage Requests
        </Typography>

        {/* Filters */}
        <Grid container spacing={3} sx={{ mb: 4 }}>
          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              label="Filter by Name"
              value={nameFilter}
              onChange={(e) => setNameFilter(e.target.value)}
            />
          </Grid>
          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel>Filter by Request Type</InputLabel>
              <Select
                value={typeFilter}
                label="Filter by Request Type"
                onChange={(e) => setTypeFilter(e.target.value)}
              >
                <MenuItem value="all">All Types</MenuItem>
                {Array.from(new Set(requests.map(r => r.subject))).map((type) => (
                  <MenuItem key={type} value={type}>
                    {type}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
        </Grid>

        {/* Charts */}
        <Grid container spacing={3} sx={{ mb: 4 }}>
          <Grid item xs={12} md={6}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                Requests by User
              </Typography>
              <Box sx={{ height: 300 }}>
                <ResponsiveContainer width="100%" height="100%">
                  <BarChart data={chartData}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="name" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="requests" fill="#8884d8" />
                  </BarChart>
                </ResponsiveContainer>
              </Box>
            </Paper>
          </Grid>

        </Grid>
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>User</TableCell>
                <TableCell>Role</TableCell>
                <TableCell>Type</TableCell>
                <TableCell>Status</TableCell>
                <TableCell>Date & Time</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {filteredRequests.map((request) => (
                <TableRow 
                  key={request._id}
                  hover
                  onClick={() => {
                    setSelectedRequest(request);
                    setDetailsDialogOpen(true);
                  }}
                  sx={{ cursor: 'pointer' }}
                >
                  <TableCell>
                    {request.user?.name}
                  </TableCell>
                  <TableCell>
                    <Chip
                      label={request.user?.role || 'N/A'}
                      color={request.user?.role === 'faculty' ? 'primary' : 'secondary'}
                      size="small"
                    />
                  </TableCell>
                  <TableCell>{request.subject}</TableCell>
                  <TableCell>
                    <Chip
                      label={request.status}
                      color={getStatusColor(request.status)}
                      size="small"
                    />
                  </TableCell>
                  <TableCell>
                    {new Date(request.createdAt).toLocaleString()}
                  </TableCell>
                  <TableCell>
                    {request.status === 'pending' && (
                      <Box onClick={(e) => e.stopPropagation()}>
                        <Button
                          color="success"
                          size="small"
                          onClick={() => handleRequestAction(request, 'approved')}
                          sx={{ mr: 1 }}
                        >
                          Approve
                        </Button>
                        <Button
                          color="error"
                          size="small"
                          onClick={() => handleRequestAction(request, 'rejected')}
                        >
                          Reject
                        </Button>
                      </Box>
                    )}
                  </TableCell>
                </TableRow>
              ))}
              {requests.length === 0 && (
                <TableRow>
                  <TableCell colSpan={6} align="center">
                    No requests found
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>
      </Box>

      {/* Response Dialog */}
      <Dialog open={dialogOpen} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
        <DialogTitle>Response to Request</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Admin Response (This will be sent via email)"
            fullWidth
            multiline
            rows={4}
            value={response}
            onChange={(e) => setResponse(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Cancel</Button>
          <Button
            onClick={() =>
              handleSubmitResponse(selectedRequest, selectedRequest.status)
            }
            color="primary"
            variant="contained"
          >
            Send Response
          </Button>
        </DialogActions>
      </Dialog>

      {/* Details Dialog */}
      <Dialog open={detailsDialogOpen} onClose={() => setDetailsDialogOpen(false)} maxWidth="md" fullWidth>
        <DialogTitle>Request Details</DialogTitle>
        <DialogContent>
          {selectedRequest && (
            <Grid container spacing={2}>
              <Grid item xs={12} md={6}>
                <Typography variant="subtitle2" color="textSecondary">User Information</Typography>
                <Typography variant="body1">{selectedRequest.user?.name}</Typography>
                <Typography variant="body2" color="textSecondary">{selectedRequest.user?.email}</Typography>
                <Chip
                  label={selectedRequest.user?.role || 'N/A'}
                  color={selectedRequest.user?.role === 'faculty' ? 'primary' : 'secondary'}
                  size="small"
                  sx={{ mt: 1 }}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <Typography variant="subtitle2" color="textSecondary">Request Information</Typography>
                <Typography variant="body1">Type: {selectedRequest.subject}</Typography>
                <Typography variant="body2">Status: 
                  <Chip
                    label={selectedRequest.status}
                    color={getStatusColor(selectedRequest.status)}
                    size="small"
                    sx={{ ml: 1 }}
                  />
                </Typography>
                <Typography variant="body2">
                  Created: {new Date(selectedRequest.createdAt).toLocaleString()}
                </Typography>
              </Grid>
              <Grid item xs={12}>
                <Typography variant="subtitle2" color="textSecondary">Description</Typography>
                <Paper variant="outlined" sx={{ p: 2, mt: 1 }}>
                  <Typography variant="body1">{selectedRequest.description}</Typography>
                </Paper>
              </Grid>
              {selectedRequest.attachmentUrl && (
                <Grid item xs={12}>
                  <Typography variant="subtitle2" color="textSecondary">Attachment</Typography>
                  <Box sx={{ mt: 2, mb: 2 }}>
                    {selectedRequest.attachmentUrl.toLowerCase().endsWith('.pdf') ? (
                      <iframe
                        src={`http://localhost:5000${selectedRequest.attachmentUrl}`}
                        width="100%"
                        height="500px"
                        title="PDF Preview"
                      />
                    ) : selectedRequest.attachmentUrl.match(/\.(jpg|jpeg|png|gif)$/i) ? (
                      <img
                        src={`http://localhost:5000${selectedRequest.attachmentUrl}`}
                        alt="Attachment"
                        style={{ maxWidth: '100%', maxHeight: '500px' }}
                      />
                    ) : (
                      <Button
                        href={`http://localhost:5000${selectedRequest.attachmentUrl}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        variant="outlined"
                        startIcon={<span role="img" aria-label="file">ðŸ“Ž</span>}
                      >
                        Download Attachment
                      </Button>
                    )}
                  </Box>
                </Grid>
              )}
              {selectedRequest.adminResponse && (
                <Grid item xs={12}>
                  <Typography variant="subtitle2" color="textSecondary">Admin Response</Typography>
                  <Paper variant="outlined" sx={{ p: 2, mt: 1 }}>
                    <Typography variant="body1">{selectedRequest.adminResponse}</Typography>
                  </Paper>
                </Grid>
              )}
            </Grid>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDetailsDialogOpen(false)}>Close</Button>
          {selectedRequest?.status === 'pending' && (
            <>
              <Button
                color="success"
                variant="contained"
                onClick={() => {
                  setDetailsDialogOpen(false);
                  handleRequestAction(selectedRequest, 'approved');
                }}
              >
                Approve
              </Button>
              <Button
                color="error"
                variant="contained"
                onClick={() => {
                  setDetailsDialogOpen(false);
                  handleRequestAction(selectedRequest, 'rejected');
                }}
              >
                Reject
              </Button>
            </>
          )}
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default AdminDashboard;
